import React from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import { Box, Typography, Container, CssBaseline } from '@mui/material';
import borgesPortrait from './assets/borges-portrait.jpg';

// Import fonts
import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';
import '@fontsource/playfair-display/400.css';
import '@fontsource/playfair-display/500.css';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1a3630',
    },
    secondary: {
      main: '#c9af8c',
    },
  },
  typography: {
    fontFamily: '"Playfair Display", "Roboto", "Helvetica", "Arial", sans-serif',
    h2: {
      fontWeight: 500,
      color: '#c9af8c',
    },
    h4: {
      fontWeight: 400,
      color: '#c9af8c',
      marginBottom: '1.5rem',
    },
    body1: {
      color: '#ffffff',
      fontSize: '1.1rem',
      lineHeight: 1.5,
    },
  },
});

const BorgesWatchmaker = () => {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          bgcolor: 'primary.main',
          minHeight: '100vh',
          display: 'flex',
          alignItems: 'center',
        }}
      >
        <Container maxWidth="lg">
          <Box
            sx={{
              display: 'flex',
              flexDirection: { xs: 'column', md: 'row' },
              alignItems: 'center',
              justifyContent: 'space-between',
              py: 4,
            }}
          >
            <Box 
              sx={{ 
                maxWidth: '600px', 
                pr: { md: 4 }, 
                mb: { xs: 4, md: 0 },
                order: { xs: 2, md: 1 } 
              }}
            >
              <Typography variant="h2" component="h1" gutterBottom>
                Borges
              </Typography>
              <Typography variant="h4" component="h2">
                O seu relojoeiro!
              </Typography>
              <Typography variant="body1">
                Descubra a excelência em consertos de relógio. Com mais de 60 
                anos de experiência atuando em Uberlândia e região.
              </Typography>
            </Box>
            <Box
              sx={{
                width: { xs: '100%', md: '40%' },
                display: 'flex',
                justifyContent: { xs: 'center', md: 'flex-end' },
                order: { xs: 1, md: 2 },
                mb: { xs: 3, md: 0 }
              }}
            >
              <Box
                component="img"
                src={borgesPortrait}
                alt="Senhor Borges, relojoeiro"
                sx={{
                  maxWidth: '100%',
                  height: 'auto',
                  maxHeight: '500px',
                  borderRadius: '8px',
                  boxShadow: 3,
                }}
              />
            </Box>
          </Box>
        </Container>
      </Box>
    </ThemeProvider>
  );
};

export default BorgesWatchmaker;
